import { useState, useEffect, useContext } from "react";
import axios from "axios";
import BestSellers from "../../components/Common/BestSellers";
import Collection from "../../components/Common/Collection";
import SpecialOffer from "../../components/Common/SpecialOffer";
import NewArrivals from "../../components/Common/NewArrivals";
import SpecialProducts from "../../components/Common/SpecialProducts";
import Testimonial from "../../components/Common/Testimonial";
import LatestNews from "../../components/Common/LatestNews";
import Partner from "../../components/Common/Partner";
import Footer from "../../components/Footer/Footer";
import SupportTwo from "../../components/Common/SupportTwo";
import Banner from "../../components/Home/Home-four/Banner";
import FeaturedArea from "../../components/Home/Home-four/FeaturedArea";
import QuickView from "../../components/Products/QuickView";
import Preloader from "../../components/Common/Preloader";

import cartContext from "../../contexts/cart-context";

function HomeFour() {
  const [products, setProducts] = useState([]);
  const [product, setProduct] = useState({});
  const [isOpen, setIsOpen] = useState(false);
  const [quantity, setQuantity] = useState(1);
  const [isLoading, setisLoading] = useState(true);
  const context = useContext(cartContext);

  useEffect(() => {
    axios
      .get("/products/")
      .then((res) => {
        setProducts(res.data.products);
      })
      .catch((err) => console.log(err));
  }, []);

  useEffect(() => {
    setTimeout(() => {
      setisLoading(false);
    }, 1000);
  }, []);

  const showQuickView = (product) => {
    setIsOpen(true);
    setProduct(product);
  };

  const closeModal = () => {
    setIsOpen(false);
    setProduct({});
  };

  const addToCart = (product) => {
    let currentItem = {
      _id: product._id,
      name: product.name,
      price: product.price,
      type: product.type,
      total_in_stock: product.total_in_stock,
      image_public_id: product.image_public_id,
      quantity,
    };
    context.addItemToCart(currentItem);
  };

  return (
    <>
      {isLoading === true ? (
        <Preloader />
      ) : (
        <div className="home-four-wrapper">
          <div className="main-slider-area">
            <div className="container">
              <Banner />
            </div>
          </div>
          <SupportTwo paddingClass=" ptb-50" />
          <FeaturedArea />
          <BestSellers
            paddingClass="pb-20"
            products={products}
            addToCart={addToCart}
            showQuickView={showQuickView}
          />
          <Collection />

          <div className="pb-20">
            <SpecialOffer
              paddingClass=" pt-50 pb-20"
              products={products}
              addToCart={addToCart}
              showQuickView={showQuickView}
            />
          </div>

          <NewArrivals
            paddingClass="pb-20"
            products={products}
            addToCart={addToCart}
            showQuickView={showQuickView}
          />
          <SpecialProducts
            products={products}
            addToCart={addToCart}
            showQuickView={showQuickView}
          />
          <Testimonial paddingClass=" pt-50" />
          <LatestNews paddingClass="pt-50 pb-20" />
          <Footer />
          <QuickView
            isOpen={isOpen}
            closeModal={closeModal}
            product={product}
          />
        </div>
      )}
    </>
  );
}

export default HomeFour;
