import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { Image } from "cloudinary-react";
import offerProducts4 from "../../../assets/img/offer-products/offer-products-4.jpg";
import offerProducts7 from "../../../assets/img/offer-products/offer-products-7.png";
import largeOfferProducts from "../../../assets/img/offer-products/large-offer-products.png";

function OfferProductsArea({ products = [], addToCart, showQuickView }) {
  const [days, setDays] = useState("");
  const [hours, setHours] = useState("");
  const [minutes, setMinutes] = useState("");
  const [seconds, setSeconds] = useState("");

  const comingSoonTime = () => {
    let endTime = new Date("August 23, 2022 17:00:00 PDT");
    let endTimeParse = Date.parse(endTime) / 1000;
    let now = new Date();
    let nowParse = Date.parse(now) / 1000;
    let timeLeft = endTimeParse - nowParse;
    let countdays = Math.floor(timeLeft / 86400);
    let counthours = Math.floor((timeLeft - countdays * 86400) / 3600);
    let countminutes = Math.floor(
      (timeLeft - countdays * 86400 - counthours * 3600) / 60
    );
    let countseconds = Math.floor(
      timeLeft - countdays * 86400 - counthours * 3600 - countminutes * 60
    );
    if (counthours < "10") {
      counthours = "0" + counthours;
    }
    if (countminutes < "10") {
      countminutes = "0" + countminutes;
    }
    if (countseconds < "10") {
      countseconds = "0" + countseconds;
    }

    setDays(countdays);
    setHours(counthours);
    setMinutes(countminutes);
    setSeconds(countseconds);
  };

  useEffect(() => {
    setInterval(() => {
      comingSoonTime();
    }, 1000);
  }, []);

  return (
    <section className="offer-products-area pt-50 pb-20">
      <div className="container">
        <div className="section-title">
          <h2>Special Offer</h2>
        </div>

        <div className="row">
          <div className="col-lg-8 col-md-12">
            <div className="row">
              {products &&
                products.slice(0, 3).map((product) => (
                  <div className="col-lg-4 col-sm-6" key={product._id}>
                    <div className="single-offer-products-box">
                      <div className="offer-products-image">
                        <Link to={`/products-details/${product._id}`}>
                          <Image
                            key={product._id}
                            cloudName={process.env.REACT_APP_CLOUDINARY_NAME}
                            publicId={product.image_public_id}
                            width="300"
                            crop="scale"
                          />
                        </Link>
                        <div className="tag">-50%</div>

                        <ul className="offer-action">
                          <li>
                            <span
                              className="addtocart-icon-wrap"
                              onClick={() => addToCart(product)}
                            >
                              <i className="flaticon-shopping-cart"></i>
                            </span>
                          </li>
                          <li>
                            <a href="#">
                              <i className="flaticon-heart"></i>
                            </a>
                          </li>
                          <li>
                            <span
                              className="quick-icon"
                              onClick={() => showQuickView(product)}
                            >
                              <i className="flaticon-view"></i>
                            </span>
                          </li>
                        </ul>
                      </div>

                      <div className="offer-products-content">
                        <span>Gadget</span>
                        <h3>
                          <Link to={`/products-details/${product._id}`}>
                            {product.name}
                          </Link>
                        </h3>
                        <div className="price">
                          <span className="new-price">${product.price}</span>
                          <span className="old-price">Rs72000.00</span>
                        </div>
                        <ul className="rating">
                          <li>
                            <i className="bx bxs-star"></i>
                            <i className="bx bxs-star"></i>
                            <i className="bx bxs-star"></i>
                            <i className="bx bxs-star"></i>
                            <i className="bx bxs-star"></i>
                          </li>
                        </ul>
                      </div>
                    </div>
                  </div>
                ))}

              <div className="col-lg-12 col-md-12">
                <div className="offer-overview bg-b9e0df">
                  <div className="row align-items-center">
                    <div className="col-lg-6">
